select
from t_api_log where id in
#{item}

SELECT COUNT(DISTINCT client_uuid) as increment FROM t_api_log WHERE
DATEDIFF(req_time,now()) = #{gap}
DATEDIFF(req_time,now()) <= 0 AND DATEDIFF(req_time,now()) >= -#{days}
AND client_uuid is not null AND client_uuid not in ( SELECT DISTINCT client_uuid FROM t_api_log WHERE
DATEDIFF(req_time,NOW()) < #{gap}
DATEDIFF(req_time,NOW()) < -#{days}
AND client_uuid is not null ) ORDER BY client_uuid

SELECT
    COUNT(id)
FROM
    t_api_log
WHERE
    DATEDIFF(`req_time`,now()) = #{gap}
  AND `client_uuid` is not null

    AND `module`=#{module}


    AND `api_name`=#{apiName}


