mapper.select:
select
  id,
  article_title,
  cover_img,
  like_num,
  browse_num,
  created_at,
  updated_at,
  content
from t_article


        AND id = #{queryDTO.id}


        AND article_title = #{queryDTO.articleTitle}


        AND cover_img = #{queryDTO.coverImg}


        AND like_num = #{queryDTO.likeNum}


        AND browse_num = #{queryDTO.browseNum}


        AND created_at = #{queryDTO.createdAt}


        AND updated_at = #{queryDTO.updatedAt}


        AND content = #{queryDTO.content}



        AND create_time >= #{queryDTO.createTimeBegin}


        AND create_time <= #{queryDTO.createTimeEnd}



        AND update_time >= #{queryDTO.updateTimeBegin}


        AND update_time <= #{queryDTO.updateTimeEnd}

select
   id,
   article_title,
   cover_img,
   like_num,
   browse_num,
   created_at,
   updated_at,
   content
from t_article


        AND id = #{queryDTO.id}


        AND article_title = #{queryDTO.articleTitle}


        AND cover_img = #{queryDTO.coverImg}


        AND like_num = #{queryDTO.likeNum}


        AND browse_num = #{queryDTO.browseNum}


        AND created_at = #{queryDTO.createdAt}


        AND updated_at = #{queryDTO.updatedAt}


