mapper.select:
select
  id,
  blockchain,
  remark,
  link,
  background_color,
  created_at,
  updated_at
from t_blockchain


        AND blockchain LIKE concat('%',#{queryDTO.blockchain},'%')


        AND remark LIKE concat('%',#{queryDTO.remark},'%')


        AND link = #{queryDTO.link}



        AND create_time >= #{queryDTO.createTimeBegin}


        AND create_time <= #{queryDTO.createTimeEnd}



        AND update_time >= #{queryDTO.updateTimeBegin}


        AND update_time <= #{queryDTO.updateTimeEnd}

select
   id,
   blockchain,
   remark,
   link,
   background_color,
   created_at,
   updated_at
from t_blockchain


        AND blockchain = #{queryDTO.blockchain}


        AND remark = #{queryDTO.remark}


        AND link = #{queryDTO.link}



        AND create_time >= #{queryDTO.createTimeBegin}


        AND create_time <= #{queryDTO.createTimeEnd}



        AND update_time >= #{queryDTO.updateTimeBegin}


        AND update_time <= #{queryDTO.updateTimeEnd}
