mapper.select:
select
DISTINCT sp.id,
sp.`name`,
sp.cover_img,
sp.remark,
sp.market_model,
sp.browse_num,
sp.like_num,
sp.created_at,
sp.updated_at
FROM t_shucang_platform sp
LEFT JOIN t_sc_bc sb ON sb.sc_id = sp.id
LEFT JOIN t_sc_tag st ON st.sc_id = sp.id
LEFT JOIN t_tag t ON st.tag_id = t.id


        AND sp.`name` LIKE concat('%',#{queryDTO.name},'%')


        AND sp.cover_img = #{queryDTO.coverImg}


        AND sp.remark LIKE concat('%',#{queryDTO.remark},'%')


        AND sp.market_model = #{queryDTO.marketModel}



        AND sp.create_time >= #{queryDTO.createTimeBegin}


        AND sp.create_time <= #{queryDTO.createTimeEnd}



        AND sp.update_time >= #{queryDTO.updateTimeBegin}


        AND sp.update_time <= #{queryDTO.updateTimeEnd}



        AND t.tag_name = #{queryDTO.tagName}



        AND sb.bc_id in

            #{blockchainId}

select

from t_shucang_platform


        AND name = #{queryDTO.name}


        AND cover_img = #{queryDTO.coverImg}


        AND remark = #{queryDTO.remark}


        AND market_model = #{queryDTO.marketModel}



        AND create_time >= #{queryDTO.createTimeBegin}


        AND create_time <= #{queryDTO.createTimeEnd}



        AND update_time >= #{queryDTO.updateTimeBegin}


        AND update_time <= #{queryDTO.updateTimeEnd}

select
from t_shucang_platform where id in
#{item}
