mapper.select:
select
  id,
  tag_name,
  tag_type,
  link,
  background_color,
  created_at,
  updated_at
from t_tag


        AND tag_name = #{queryDTO.tagName}


        AND tag_type = #{queryDTO.tagType}


        AND link = #{queryDTO.link}



        AND create_time >= #{queryDTO.createTimeBegin}


        AND create_time <= #{queryDTO.createTimeEnd}



        AND update_time >= #{queryDTO.updateTimeBegin}


        AND update_time <= #{queryDTO.updateTimeEnd}

